import axios from 'axios';
import React, { useState } from 'react';
import './Search.css';
import Thumbnail from '../Thumbnail';

export default function Search({ isOpen, close }) {
    const filteredContent = (data) => {
        data = data.filter((el) => {
            return el.nickName.indexOf(searchKeyword) > -1;
        });
        return data.map((el) => {
            return <Thumbnail />;
        });
    };
    const [contentList, setContentList] = useState([]);
    const [searchKeyword, SetSearchKeyword] = useState('');
    const inputHandler = (e) => {
        SetSearchKeyword({ [e.target.name]: e.target.value });
    };
    const searchRefresh = () => {
        SetSearchKeyword('');
    };
    const getContentList = () => {
        axios.get('https://localhost:4000/content').then((res) => {
            setContentList(res.data.content);
        });
    };
    getContentList();
    return (
        <>
            {isOpen ? (
                <div className="searchModal">
                    <div className="searchModal">
                        <div>
                            <input
                                placeholder="검색하기"
                                name="searchKeyword"
                                onChange={inputHandler}
                                value={searchKeyword}></input>
                            <button className="closeBtn" onClick={close}>
                                X
                            </button>
                            <div>{contentList ? <div>{/* <Thumbnail /> */}</div> : null}</div>
                        </div>
                    </div>
                </div>
            ) : null}
        </>
    );
}
