import styled from 'styled-components';

export const ModalContainer = styled.div`
background-color: white;
text-align: center;
vertical-align: middle;
padding: 100px;
`;

export const ModalBtn = styled.button`
  background-color: #4000c7;
  text-decoration: none;
  border: none;
  padding: 20px;
  color: white;
  border-radius: 30px;
  cursor: grab;
`;

export const ModalView = styled.button`
  background-color: white;
  text-decoration: none;
  border: none;
  padding: 40px;
  color: #4000c7;
  border-radius: 10px;
  cursor: grab;
  vertical-align: middle;
  // TODO : Modal창 CSS를 구현
  `;

export const Modal = () => {
    const [isLogin, setIsLogin] = useState(false);

    const loginModalHandler = () => {
        setisLogin(!isLogin)
        // isopen 상태 변경 메소드
    };

    return 
    <>
        <ModalContainer onClick={loginModalHandler}>
            <ModalBtn onClick={loginModalHandler}>

            </ModalBtn>
        </ModalContainer>
    </>
}
