import React, { useState, Component } from 'react';
import axios from 'axios';



class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
        };
        this.inputHandler = this.inputHandler.bind(this);
        this.loginRequestHandler = this.loginRequestHandler.bind(this);
    }
    inputHandler(e) {
        this.setState({ [e.target.name]: e.target.value}) // input창 네임에 맞는 벨류로 스테이트 변경
    }
    loginRequestHandler() {
        axios
        .post(
            'https://localhost:4000/login', 
            {
                email: this.state.email,
                password: this.state.password,
            },
            {'Content-Type': 'applicaion/json', withCredentials: true}
        )
        .then((res) => {
            this.props.loginHandler(true);
            return axios.get('https://localhost:4000/user')
        })
    }


    render() {
        const { isOpen, close } =this.props; // button에서 props
        return (
            <>
            {isOpen === true ? (
                <div className="modal">
                    <div onClick={close}>
                        <div className="loginModal">
                            <span className="close" onClick={close}>

                            </span>
                        </div>
                    </div>
                </div>
            ) : null}
            </>
            )
    }
}
