import React, { Component } from 'react';
import './SignUpModal.css';
import axios from 'axios';

class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            picture: '',
            provider: '',
            nickName: '',
            email: '',
            password: '',
            mobile: '',
        };
        this.inputHandler = this.inputHandler.bind(this);
        this.signUpRequestHandler = this.signUpRequestHandler.bind(this);
        this.fileEvent = this.fileEvent.bind(this);
    }
    fileEvent = (e) => {
        const reader = new FileReader();
        reader.onload = () => {
            this.setState({
                picture: e.target.picture,
            });
            console.log('파일 업로드 완료.');
        };
        reader.readAsText(e.target.files[0]);
    };

    inputHandler(e) {
        this.setState({
            [e.target.name]: e.target.value,
        }); // input창 네임에 맞는 벨류로 스테이트 변경
    }

    signUpRequestHandler() {
        axios
            .post(
                'https://localhost:4000/signup',
                {
                    picture: this.state.picture,
                    provider: this.state.provider,
                    nickName: this.state.nickName,
                    email: this.state.email,
                    password: this.states.password,
                    mobile: this.state.mobile,
                },
                { 'Content-Type': 'application/json', withCredentials: true },
            )
            .then((res) => {
                return axios.get();
            });
    }

    render() {
        const { isOpen, close } = this.props; // button에서 props
        return (
            <>
                {isOpen === true ? (
                    <div className="modal">
                        <div onClick={close}>
                            <div className="signupModal">
                                <button className="closeBtn" onClick={close}>
                                    X
                                </button>
                                <div className="modalContents" onClick={isOpen}>
                                    <img className="signUpIcon" />
                                    <input
                                        name="email"
                                        className="signUpId"
                                        type="text"
                                        placeholder="email"
                                        onChange={(e) => this.inputHandler(e)}
                                        value={this.state.email}
                                    />
                                    <input
                                        name="password"
                                        className="signUpPw"
                                        type="password"
                                        placeholder="password"
                                        onChange={(e) => this.inputHandler(e)}
                                        value={this.state.password}
                                    />
                                    <input
                                        name="nickName"
                                        className="signUpNickName"
                                        type="text"
                                        placeholder="nickname"
                                        onChange={(e) => this.inputHandler(e)}
                                        value={this.state.nickName}
                                    />
                                    <input
                                        name="mobile"
                                        className="signUpMobile"
                                        type="text"
                                        placeholder="-없이 숫자만 입력하세요"
                                        onChange={(e) => this.inputHandler(e)}
                                        value={this.state.mobile}
                                    />
                                    <div className="profileUploader">프로필 사진을 선택해 보세요.</div>
                                    <input
                                        name="picture"
                                        className="signUpPic"
                                        type="file"
                                        placeholder="picture"
                                        onChange={(e) => this.fileEvent(e)}
                                        value={this.state.picture}
                                    />
                                    <button className="signUpB" onClick={this.loginRequestHandler}>
                                        {' '}
                                        회원가입
                                    </button>
                                    <div className="loginLine">
                                        회원이신가요?
                                        <button className="loginB" onClick={this.handleLoginOrSignupModal}>
                                            로그인
                                        </button>
                                        {this.state.isLoginOrSignupModalOn && (
                                            <Signup handleLoginOrSignupModal={this.handleLoginOrSignupModal} />
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ) : null}
            </>
        );
    }
}

export default Signup;
