import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './loginModal.css';
import axios from 'axios';

class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoginOrSignupModalOn: false,
        };
    }
}

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            isLoginOrSignupModalOn: false,
        };
        this.inputHandler = this.inputHandler.bind(this);
        this.loginRequestHandler = this.loginRequestHandler.bind(this);
    }
    handleLoginOrSignupModal(e) {
        this.setState({
            isLoginOrSignupModalOn: !this.state.isLoginOrSignupModalOn,
        });
    }

    inputHandler(e) {
        this.setState({
            [e.target.name]: e.target.value,
        }); // input창 네임에 맞는 벨류로 스테이트 변경
    }

    loginRequestHandler() {
        axios
            .post(
                'https://localhost:4000/login',
                {
                    email: this.state.email,
                    password: this.state.password,
                },
                {
                    'Content-Type': 'application/json',
                    withCredentials: true,
                },
            )
            .then((res) => {
                this.props.loginHandler(true);
                return axios.get('https://localhost:4000/user', {
                    withCredentials: true,
                });
            })
            .then((res) => {
                let { id, nickname, phone, email, profile_img } = res.data.userInfo;
                this.props.setUserInfo({
                    id,
                    nickname,
                    email,
                    phone,
                    profile_img,
                });
            })
            .then((res) => {
                let { id, title, writer, picture_1, picture_2, description, voting_deadline, created_at, update_at } =
                    res.data.content;
                this.props.setContent({
                    id,
                    title,
                    writer,
                    picture_1,
                    picture_2,
                    description,
                    voting_deadline,
                    created_at,
                    update_at,
                });
            })
            .catch((err) => alert(err));
    }

    render() {
        const { isOpen, close } = this.props; // button에서 props
        return (
            <>
                {isOpen === true ? (
                    <div className="modal">
                        <div onClick={close}>
                            <div className="loginModal">
                                <button className="closeBtn" onClick={close}>
                                    X
                                </button>
                                <div className="modalContents" onClick={isOpen}>
                                    <img className="signInIcon" />
                                    <input
                                        name="email"
                                        className="loginId"
                                        type="text"
                                        placeholder="email"
                                        onChange={(e) => this.inputHandler(e)}
                                        value={this.state.email}
                                    />
                                    <input
                                        name="password"
                                        className="loginPw"
                                        type="password"
                                        placeholder="password"
                                        onChange={(e) => this.inputHandler(e)}
                                        value={this.state.password}
                                    />
                                    <button className="loginBtn" onClick={this.loginRequestHandler}>
                                        {' '}
                                        로그인
                                    </button>
                                    <button className="kakaoBtn">
                                        <img
                                            className="kakaoLogo"
                                            src="https://developers.kakao.com/tool/resource/static/img/button/kakaolink/kakaolink_btn_medium.png"
                                        />
                                        <div className="kakaoText">카카오 계정으로 가입</div>
                                    </button>
                                    <div className="loginLine">
                                        회원이 아니신가요?
                                        <button className="signUpBtn" onClick={this.handleLoginOrSignupModal}>
                                            회원가입
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ) : null}
            </>
        );
    }
}

export default Login;
