import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import './Mypage.css';

export default function Mypage(props) {
    const info = props.userInfo;
    const [isClick, setIsClick] = useState(false);
    const [patchInfo, setPatchInfo] = useState({
        nickName: '',
        email: '',
        password: '',
        profile_img: '',
        phone: '',
    });
    const clickHandler = () => {
        setIsClick(!isClick);
    };

    const inputHandler = (e) => {
        setPatchInfo({ ...patchInfo, [e.target.name]: e.target.value });
    };
    const patchRequestHandler = () => {
        if (
            !patchInfo.nickname ||
            !patchInfo.email ||
            !patchInfo.password ||
            !patchInfo.email ||
            !patchInfo.profile_img ||
            !patchInfo.phone
        ) {
        } else {
            axios.post('https://localhost:4000/user/:id', {});
        }
    };

    return (
        <>
            {isClick ? (
                <form>
                    <div className="pf-input-area">
                        <a className="profile_img">
                            <div className="label">프로필 사진</div>
                            <input
                                name="picture"
                                type="file"
                                className="avatar"
                                onChange={(e) => inputHandler(e)}
                                value={patchInfo.profile_img}></input>
                        </a>
                        <div className="id">
                            <div className="label">아이디</div>
                            <div>info.id</div>
                        </div>
                        <div className="password">
                            <div className="label">비밀번호</div>
                            <input
                                name="password"
                                type="password"
                                placeholder="비밀번호를 입력해주세요"
                                onChange={(e) => inputHandler(e)}
                                value={patchInfo.password}></input>
                        </div>
                        <div className="nickName">
                            <div className="label">닉네임</div>
                            <input
                                name="nickName"
                                type="text"
                                maxLength="20"
                                placeholder="닉네임을 입력해주세요"
                                onChange={(e) => inputHandler(e)}
                                value={patchInfo.nickName}></input>
                        </div>
                        <div className="email">
                            <div className="label">이메일</div>
                            <input
                                name="email"
                                type="text"
                                maxLength="20"
                                placeholder="이메일을 입력해주세요"
                                onChange={(e) => inputHandler(e)}
                                value={patchInfo.email}></input>
                        </div>
                        <div className="phone">
                            <div className="label">전화번호</div>
                            <input
                                name="phone"
                                type="text"
                                maxLength="20"
                                placeholder="전화번호를 입력해주세요"
                                onChange={(e) => inputHandler(e)}
                                value={patchInfo.phone}></input>
                        </div>
                        <input type="submit" value="저장" />
                    </div>
                </form>
            ) : (
                <div>
                    <h1>안녕하세요 info.name님</h1>
                    <div className="pfArea">
                        <a className="pfImg">
                            <div className="label">프로필 사진</div>
                            <div type="file" className="avatar">
                                이미지
                            </div>
                        </a>
                        <div className="id">
                            <div className="label">아이디</div>
                            <div className="id">info.id</div>
                        </div>
                        <div className="nickName">
                            <div className="label">닉네임</div>
                            <div>info.nickName</div>
                        </div>
                        <div className="email">
                            <div className="label">이메일</div>
                            <div>info.email</div>
                        </div>
                        <div className="phone">
                            <div className="label">전화번호</div>
                            <div>info.phone</div>
                        </div>
                    </div>
                    <button classname="editBtn" onClick={clickHandler}>
                        수정
                    </button>
                </div>
            )}
        </>
    );
}
