import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './loginModal.css';
import axios from 'axios';

class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            picture: '',
            provider: '',
            nickName: '',
            email: '',
            password: '',
            mobile: '',
        };
        this.inputHandler = this.inputHandler.bind(this);
        this.signUpRequestHandler = this.signUpRequestHandler.bind(this);
    }

    inputHandler(e) {
        this.setState({
            [e.target.name]: e.target.value,
        }); // input창 네임에 맞는 벨류로 스테이트 변경
    }

    signUpRequestHandler() {
        axios
            .post(
                'https://localhost:4000/signup',
                {
                    picture: this.state.picture,
                    provider: this.state.provider,
                    nickName: this.state.nickName,
                    email: this.state.email,
                    password: this.states.password,
                    mobile: this.state.mobile,
                },
                { 'Content-Type': 'application/json', withCredentials: true },
            )
            .then((res) => {
                return axios.get();
            });
    }

    render() {
        const { isOpen, close } = this.props; // button에서 props
        return (
            <>
                <Form>
                    {isOpen === true ? (
                        <div className="modal">
                            <div onClick={close}>
                                <div className="loginModal">
                                    <button className="closeBtn" onClick={close}>
                                        X
                                    </button>
                                    <div className="modalContents" onClick={isOpen}>
                                        <img className="signInIcon" />
                                        <input
                                            name="email"
                                            className="loginId"
                                            type="text"
                                            placeholder="email"
                                            onChange={(e) => this.inputHandler(e)}
                                            value={this.state.email}
                                        />
                                        <input
                                            name="password"
                                            className="loginPw"
                                            type="password"
                                            placeholder="password"
                                            onChange={(e) => this.inputHandler(e)}
                                            value={this.state.password}
                                        />
                                        <button className="loginBtn" onClick={this.loginRequestHandler}>
                                            {' '}
                                            로그인
                                        </button>
                                        <button className="kakaoBtn">
                                            <img
                                                className="kakaoLogo"
                                                src="https://developers.kakao.com/tool/resource/static/img/button/kakaolink/kakaolink_btn_medium.png"
                                            />
                                            <div className="kakaoText">카카오 계정으로 가입</div>
                                        </button>
                                        <div className="loginLine">
                                            회원이 아니신가요?
                                            <Link to="/signup">회원가입</Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ) : null}
                </Form>
            </>
        );
    }
}

export default Signup;
