import React, { useState } from 'react';
import axios from 'axios';
import './LoginModal.css';
import { Link, Route, Router } from 'react-router-dom';
import Signup from './SignUpModal';

function Login({ isOpen, close, open }) {
    const [loginInfo, setLoginInfo] = useState({
        email: '',
        password: '',
    });
    const [isLogin, setIsLogin] = useState({
        isLogin: false,
    });
    const [isLoginOrSignupModalOn, setIsLoginOrSignupModalOn] = useState(false);
    const inputHandler = (e) => {
        setLoginInfo({ ...loginInfo, [e.target.name]: e.target.value });
    };
    const loginHandler = () => {
        setIsLogin(true);
    };
    const handleLoginOrSignupModal = () => {
        setIsLoginOrSignupModalOn(true);
    };
    const loginRequestHandler = () => {
        axios
            .post(
                'https://localhost:4000/login',
                {
                    email: loginInfo.email,
                    password: loginInfo.password,
                },
                { 'Content-Type': 'applicarion/json', withCredentials: true },
            )
            .then((res) => {
                if (res.message === 'ok') return loginHandler(true);
            })
            .catch((err) => alert(err));
    };
    return (
        <>
            {isOpen === true ? (
                <div className="modal">
                    <div className="loginModal">
                        <button className="closeBtn" onClick={close}>
                            X
                        </button>
                        <div className="modalContents">
                            <input
                                name="email"
                                className="loginId"
                                type="text"
                                placeholder="email"
                                onChange={(e) => inputHandler(e)}
                                value={loginInfo.email}
                            />
                            <input
                                name="password"
                                className="loginPw"
                                type="password"
                                placeholder="password"
                                onChange={(e) => inputHandler(e)}
                                value={loginInfo.password}
                            />
                            <button className="loginBtn" onClick={loginRequestHandler}>
                                로그인
                            </button>
                            <button className="kakaoBtn">
                                <img
                                    className="kakaoLogo"
                                    src="https://developers.kakao.com/tool/resource/static/img/button/kakaolink/kakaolink_btn_medium.png"
                                />
                                <div className="kakaoText">카카오 계정으로 가입</div>
                            </button>
                            <div className="signUpLine">
                                회원이 아니신가요?
                                {isLoginOrSignupModalOn === true ? (
                                    <Signup handleLoginOrSignupModal={handleLoginOrSignupModal} />
                                ) : (
                                    <Login />
                                )}
                                <button className="signupBtn" onClick={handleLoginOrSignupModal}>
                                    회원가입
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            ) : null}
        </>
    );
}

export default Login;
