import React, { Component } from 'react';
import { Link } from "react-router-dom";
import styled from 'styled-components';

class SignIn extends Component {
    state = {
      email: "",
      password: "",
    };

    loginHandler = (e) => {
      const { name, value } = e.target;
      this.setState({ [name]: value });
    };   ////계산된 속성명 사용

    loginClickHandler = () => {
      const { email, password } = this.state;
      fetch("http://10.58.2.17:8000/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email,
          password,
        }),
      })
        .then((res) => res.json())
        .then((res) => console.log(res));
    }; 

    render() {
      const { isOpen, close } = this.props;   //아까 버튼에서 props로 가져온것
      return (
        <>
          {isOpen ? (
            <div className="modal">
              <div onClick={close}>
                <div className="loginModal">
                  <span className="close" onClick={close}>
                    &times;
                  </span>
                  <div className="modalContents" onClick={isOpen}>
                    <img
                      className="signinIcon"
                      src="/Images/SignIn/signinIcon.png"
                    />
                    <input
                      name="email"
                      className="loginId"
                      type="text"
                      placeholder="아이디"
                      onChange={this.loginHandler}
                    />
                    <input
                      name="password"
                      className="loginPw"
                      type="password"
                      placeholder="비밀번호"
                      onChange={this.loginHandler}
                    />
                    <div className="loginMid">
                      <label className="autoLogin" for="hint">
                        {" "}
                        <input type="checkbox" id="hint" /> 로그인 유지하기
                      </label>
                      <div className="autoLogin">아이디/비밀번호 찾기</div>
                    </div>
                    <button className="loginBtn" onClick={this.loginClickHandler}>
                      {" "}
                      로그인{" "}
                    </button>
                    <div className="socialBox">
                      <div className="kakao">
                        <img
                          className="kakaoLogo"
                          src="/Images/SignIn/kakao.png"
                        />
                        <div className="kakaoText">카카오 계정으로 신규가입</div>
                      </div>
                      <div className="facebook">
                        <img
                          className="facebookLogo"
                          src="/Images/SignIn/facebook.png"
                        />
                        <div className="facebookText">
                          페이스북 계정으로 신규가입
                        </div>
                      </div>
                    </div>
                    <div className="loginEnd">
                      <div className="loginLine">
                        회원이 아니신가요? <Link to="/signup">회원가입</Link>
                      </div>
                      <div className="noUser">비회원 주문 조회</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ) : null}
        </>
      );
    }
  }

  export default SignIn;
