import './App.css';
import { useReducer, useState } from 'react';
import { BrowserRouter, Switch, Route, Redirect } from 'react-router-dom';
import Nav from './components/Nav';
import Thumbnail from './components/Thumbnail';
import axios from 'axios';
import SignupPage from './pages/SignUp/SignUpPage';
import LoginButton from './components/modals/LoginBtn';
import Sidebar from './components/SideBar/SideBar';
import SignUpButton from './components/modals/SignUpBtn';
import Login from './components/modals/LoginModal';
//import Mypage from './pages/Mypage.js';
//import Login from './components/Login.js';
//import NewContent from './pages/Newcontent.js';
//import CurContent from './pages/CurContent.js';

export default function App() {
    const [isLogin, setIsLogin] = useState(false);

    const loginHandler = function () {
        setIsLogin(true);
    };

    const [contentList, setContentList] = useState([]);

    const getContentList = () => {
        axios.get('https://localhost:4000/content').then((res) => {
            setContentList(res.data.content);
        });
    };

    getContentList();

    return (
        <>
            <BrowserRouter>
                <LoginButton loginHandler={loginHandler} />
                <Sidebar />
                <SignUpButton loginHandler={loginHandler} />
                <Route path="/" component={Login} />
                <Route exact path="/signup" component={SignupPage} />
            </BrowserRouter>
        </>
    );
}
