import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const TabMenu = styled.ul`
    background-color: rgba(102, 102, 102, 0.5);
    font-weight: bold;
    display: flex;
    flex-direction: row;
    justify-items: center;
    align-items: center;
    list-style: none;
    margin-bottom: 7rem;

    .submenu {
        width: 100%;
        padding: 15px;
        cursor: pointer;
    }
    .mypage {
        width: auto;
        padding: 20px;
        float: left;
        cursor: pointer;
    }

    .focused {
        background-color: #4000c7;
        transition: all 0.3s ease;
        color: white;
    }

    & div.desc {
        text-align: center;
    }
`;

const Desc = styled.div`
    text-align: center;
`;
export default function Mypage(props) {
    const info = props.userInfo;
    const [currentTab, setCurrentTab] = useState(0);
    const tabMenu = [
        {
            name: 'mypage',
            content: (
                <div>
                    <div className="id">info.id</div>
                    <div className="nickName">info.nickName</div>
                    <div className="email">info.email</div>
                    <div className="phone">info.phone</div>
                    <div className="profile_img">info.profile_img</div>
                </div>
            ),
        },
        { name: 'mycontent', content: 'mycontent ok' },
    ];
    const selectMenuHandler = (index) => {
        setCurrentTab(index);
    };
    return (
        <div>
            <center>
                <h1>Nav 자리</h1>
                <div>
                    <TabMenu>
                        {tabMenu.map((el, index) => {
                            return (
                                <li
                                    className={currentTab === index ? 'mypage' : 'mycontent'}
                                    onClick={() => selectMenuHandler(index)}>
                                    {el.name}
                                </li>
                            );
                        })}
                    </TabMenu>
                    <div>
                        안녕하세요. <span className="name">00</span>님! 로그인이 완료되었습니다.
                    </div>
                </div>
                <Desc>
                    <p>{tabMenu[currentTab].content}</p>
                </Desc>
            </center>
        </div>
    );
}
