import React, { useState } from 'react';
import axios from 'axios';
import './LoginModal.css';
import { Link } from 'react-router-dom';

function Login({ isOpen, close }) {
    const [loginInfo, setLoginInfo] = useState({
        email: '',
        password: '',
    });
    const [isLogin, setIsLogin] = useState({
        isLogin: false,
    });
    const inputHandler = (e) => {
        setLoginInfo({ ...loginInfo, [e.target.name]: e.target.value });
    };
    const loginHandler = () => {
        setIsLogin(true);
    };
    const loginRequestHandler = () => {
        axios
            .post(
                'https://localhost:4000/login',
                {
                    email: loginInfo.email,
                    password: loginInfo.password,
                },
                { 'Content-Type': 'applicarion/json', withCredentials: true },
            )
            .then((res) => {
                if (loginHandler === true)
                    return axios.get('https://localhost:4000/content', {
                        withCredentials: true,
                    });
            });
    };
    return (
        <>
            {isOpen === true ? (
                <div className="modal">
                    <div onClick={close}>
                        <div className="signupModal">
                            <button className="closeBtn" onClick={close}>
                                X
                            </button>
                            <div className="modalContents" onClick={isOpen}>
                                <img className="signUpIcon" />
                                <input
                                    name="email"
                                    className="signUpId"
                                    type="text"
                                    placeholder="email"
                                    onChange={(e) => this.inputHandler(e)}
                                    value={this.state.email}
                                />
                                <input
                                    name="password"
                                    className="signUpPw"
                                    type="password"
                                    placeholder="password"
                                    onChange={(e) => this.inputHandler(e)}
                                    value={this.state.password}
                                />
                                <input
                                    name="nickName"
                                    className="signUpNickName"
                                    type="text"
                                    placeholder="nickname"
                                    onChange={(e) => this.inputHandler(e)}
                                    value={this.state.nickName}
                                />
                                <input
                                    name="mobile"
                                    className="signUpMobile"
                                    type="tel"
                                    placeholder="-없이 숫자만 입력하세요"
                                    onChange={(e) => this.inputHandler(e)}
                                    value={this.state.mobile}
                                />
                                <div className="profileUploader">프로필 사진을 선택하세요.</div>
                                <input
                                    name="picture"
                                    className="signUpPic"
                                    type="file"
                                    placeholder="picture"
                                    onChange={(e) => this.fileEvent(e)}
                                    value={this.state.picture}
                                />
                                <button className="signUpB" onClick={this.loginRequestHandler}>
                                    {' '}
                                    회원가입
                                </button>
                                <div className="loginLine">
                                    회원이신가요?
                                    <button className="loginB" onClick={this.handleLoginOrSignupModal}>
                                        로그인
                                    </button>
                                    {this.state.isLoginOrSignupModalOn && (
                                        <Login handleLoginOrSignupModal={this.handleLoginOrSignupModal} />
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            ) : null}
        </>
    );
}

export default Login;
