import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import SidebarItem from './Sidebaritem';
import './SideBar.css';
<div class="btn-menu">
    <a>
        <svg width="18" height="14" viewBox="0 0 18 14" fill="none">
            <rect width="18" height="2" rx="1" fill="#89898B"></rect>{' '}
            <rect x="6" y="6" width="12" height="2" rx="1" fill="#89898B"></rect>{' '}
            <rect y="12" width="18" height="2" rx="1" fill="#89898B"></rect>
        </svg>
    </a>
</div>;
function Sidebar() {
    const menus = [
        { name: '홈', path: '/' },
        { name: '회원가입', path: '/signup' },
        { name: '새 글 작성', path: '/content' },
    ];
    const [isOpen, setisOpen] = useState(false);
    const sidebarHandler = () => {
        setisOpen(!isOpen);
    };
    return (
        <div className="sidebar">
            <img
                className="sidebarIcon"
                src="https://developers.kakao.com/tool/resource/static/img/button/kakaolink/kakaolink_btn_medium.png"
                onClick={sidebarHandler}
            />
            {isOpen === true ? (
                <div className="back" onClick={sidebarHandler}>
                    <div className="sidebarContents">
                        <span>
                            {' '}
                            <button className="closeBtn" onClick={sidebarHandler}>
                                X
                            </button>
                        </span>
                        {menus.map((menu, index) => {
                            return (
                                <Link to={menu.path} key={index}>
                                    <SidebarItem menu={menu} />
                                </Link>
                            );
                        })}
                    </div>
                </div>
            ) : null}
        </div>
    );
}
export default Sidebar;
